import React, { Fragment } from 'react'
import { Row, Col } from 'react-materialize'


class SnippetView extends React.Component {
  state = {
      html: '<div>hello</div>',
     divStyle: {
      margin: '40px',
      background: 'black',
      border: '5px solid pink',
    },
    pStyle: {
      fontSize: '15px',
      textAlign: 'center',
    }
  }
  formatHTML = () => {
    this.state.html.replace(/>/, 'style={this.state.divStyle}')
  }
  render() {
    return (
      <Fragment>
        {this.state.html}
      </Fragment>
    )
  }
}

export default SnippetView;
