import React, { Component } from 'react';
import './App.css';
import Header from './Components/Header'
import {Row, Col, Input} from 'react-materialize'
import SnippetContainer from './Containers/SnippetContainer'
import Login from './Components/Login'


class App extends Component {
  state={
    snippets: [],
    snippetForm: {
      name: '',
      html: '',
      css: '',
      user_id: '',
      tags: ''
    },
    authenticated: false,
    loginToggled: true,
    loginUsername: ""
  }

  componentDidMount(){
    fetch("http://localhost:9000/api/v1/snippets")
    .then(r=>r.json())
    .then(data=>{
      this.setState({
        snippets: data
      })
    })
  }

  handleSubmit = e => {
    e.persist()
    e.preventDefault()
    fetch("http://localhost:9000/api/v1/snippets", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
        Accept: "application/json"
      },
      body: JSON.stringify(this.state.snippetForm)
    })
    .then(r=>r.json())
    .then(data => {
      this.setState(prevState=>{
        return {snippets: [data, ...prevState.snippets]}
      }, () => {
        this.setState({
          snippetForm: {
            name: '',
            html: '',
            css: '',
            user_id: '',
            tags: ''
          }
        },()=>{document.querySelector('.btn.waves-effect.waves-light.btn-flat.modal-action.modal-close').click()})
      })
    })


  }
  toggleLoginForm = e => {
    this.setState(prevState => {
      return {loginToggled: !prevState.loginToggled}
    })
  }

  handleChange = e => {
    e.persist()
    this.setState(prevState=>{
      return {snippetForm: {...prevState.snippetForm, [e.target.id]:e.target.value}}
    })
  }

  loginUsername = e => {
    e.persist()
    this.setState({loginUsername: e.target.value})
  }

  render() {

    return (
      <div className="App">
        <Header loginToggled={this.state.loginToggled} authenticated={this.state.authenticated} toggleLoginForm={this.toggleLoginForm} snippetForm={this.state.snippetForm} handleChange={this.handleChange} handleSubmit={this.handleSubmit} />
        <Row>
          <Col s={10} offset={'s1'}>
          {!this.state.authenticated && <Login />}
          {this.state.authenticated && <SnippetContainer snippets={this.state.snippets}/> }
        </Col>
      </Row>
      </div>
    );
  }
}

export default App;
