import React from 'react'
import { Row, Col, Button, Card, Toast, Input } from 'react-materialize'
import Iframe from 'react-iframe'


const SnippetView = ({styleSheet, snippetId, snippets, generateStyleSheet}) => {
  const div = {
    height: '33vh',
    width: '100%',
    padding: '5%',
    background: 'rgb(48,48,48)',
    border: '3px solid teal',
    color: 'white',
    borderRadius: '12px',
    overflow: 'scroll',
  }

  const bottomDiv = {
    height: '72vh',
    width: '100%',
    marginTop: '5%',
    background: 'white',
    border: '5px solid teal',
    color: 'white',
    borderRadius: '12px',
    overflow: 'scroll',
    resize: 'both',
}

  const file = {
    background: 'orange',
    padding: '3px',
    color: 'white',
  }

  const hover = e => {
    e.target.style.background = 'rgb(59,151,136)'
  }

  const hoverEnd = e => {
    e.target.style.background = 'orange'
  }

  const clickLink = e => {

    }

  const foundSnippet = () => {
    let returnSnip = snippets.find(snip =>{
      return snip.id === snippetId
    })
    return returnSnip
  }

  const copyPasta = () => {
  const copyText = document.getElementById("copy-text");
  copyText.select();
  return document.execCommand("copy");
  }
    return (
      <div>
      <Row>
      {!styleSheet &&
      <Row>
        <Col s={4} offset={'s4'}>
          <Button id={snippetId} onClick={generateStyleSheet} >Generate Stylesheet</Button>
        </Col>
      </Row>}
          {styleSheet &&
          <Row style={{margin: 'none'}}>
          <Col s={8}>
            <Input s={12} id="copy-text" label="Stylesheet" validate defaultValue={styleSheet.filename} />
          </Col>
          <Col s={4}>
            <Button s={12} onClick={() => {
              copyPasta
              window.Materialize.toast('Copied!', 1000)}
            }>
            Copy Link
            </Button>
          </Col>
          </Row>}
      </Row>
      <Row>
        <Col s={6}>
          <Row>
          <h6>HTML</h6>
          <div style={div}>
            {
            (snippets.length>0) && foundSnippet().html
            }
          </div>
          </Row>
          <Row>
          <h6>CSS</h6>
            <div style={div}>
            {
            (snippets.length>0) && foundSnippet().css
            }
            </div>
          </Row>
        </Col>
        <Col s={6}>
          <div style={bottomDiv}>
          <Iframe url={`http://localhost:9000/api/v1/render/${snippetId}?height=whatever&width=whatever`}
            width="100%"
            height="100%"
            id="myId"
            className="myClassname"
            display="initial"
            position="relative"
            allowFullScreen>
          </Iframe>
          </div>
        </Col>
        </Row>
        <Row>
        <Col s={6}>

        </Col>


      </Row>
      </div>

    )
}

export default SnippetView;
