import React from 'react'
import { Row, Col, Button, Card, Toast } from 'react-materialize'
import Iframe from 'react-iframe'


const SnippetView = ({styleSheet, snippetId, snippets, generateStyleSheet}) => {
  const div = {
    height: '33vh',
    width: '100%',
    padding: '5%',
    background: 'rgb(48,48,48)',
    border: '3px solid teal',
    color: 'white',
    borderRadius: '12px',
    overflow: 'scroll',
  }

  const bottomDiv = {
    height: '72vh',
    width: '100%',
    marginTop: '5%',
    background: 'white',
    border: '5px solid teal',
    color: 'white',
    borderRadius: '12px',
    overflow: 'scroll',
    resize: 'both',
}

  const file = {
    background: 'orange',
    padding: '3px',
    color: 'white',
  }

  const hover = e => {
    e.target.style.background = 'rgb(59,151,136)'
  }

  const hoverEnd = e => {
    e.target.style.background = 'orange'
  }

  const clickLink = e => {
    <Button onClick={() => {
            window.Materialize.toast('I am a toast!', 7000)
        }
    }>
        Click to show
    </Button>
    }

  const foundSnippet = () => {
    let returnSnip = snippets.find(snip =>{
      return snip.id === snippetId
    })
    return returnSnip
  }
    return (
      <div>
      <Row>
        <Col s={4} offset={'s4'}>
          <Button id={snippetId} onClick={generateStyleSheet} >Generate Stylesheet</Button>
        </Col>
        {styleSheet && <Col>
          <div style={file} onClick={clickLink} onMouseEnter={hover} onMouseLeave={hoverEnd}>
          {styleSheet.filename}
          </div>
        </Col>}
      </Row>
      <Row>
        <Col s={6}>
          <Row>
          <h6>HTML</h6>
          <div style={div}>
            {
            (snippets.length>0) && foundSnippet().html
            }
          </div>
          </Row>
          <Row>
          <h6>CSS</h6>
            <div style={div}>
            {
            (snippets.length>0) && foundSnippet().css
            }
            </div>
          </Row>
        </Col>
        <Col s={6}>
          <div style={bottomDiv}>
          <Iframe url={`http://localhost:9000/api/v1/render/${snippetId}?height=whatever&width=whatever`}
            width="100%"
            height="100%"
            id="myId"
            className="myClassname"
            display="initial"
            position="relative"
            allowFullScreen>
          </Iframe>
          </div>
        </Col>
        </Row>
        <Row>
        <Col s={6}>

        </Col>


      </Row>
      </div>

    )
}

export default SnippetView;
